import React, { useState } from 'react';
import '../styles/RightSidebar.css';
import online from "../assets/online.png";
import offline from "../assets/offline.png";

const RightSidebar = () => {
  const [isExpanded, setIsExpanded] = useState(false);

  const handleMouseEnter = () => setIsExpanded(true);
  const handleMouseLeave = () => setIsExpanded(false);

  return (
    <div
      className={`right-sidebar ${isExpanded ? 'expanded' : ''}`}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div className={`sidebar-section ${isExpanded ? 'expanded' : ''}`}>
        {isExpanded && <h3 className="section-title">Squad</h3>}
        <img src={online} alt='online' className="status-icon" />
        {isExpanded && <button className="squad-join">Squad Join</button>}
      </div>
      <div className={`sidebar-section ${isExpanded ? 'expanded' : ''}`}>
        {isExpanded && <h3 className="section-title">Online</h3>}
        <div className="user">
          <img src={online} alt="online" className="status-icon" />
          {isExpanded && (
            <>
              <span>MaryJane</span>
              <span className="status">Online</span>
            </>
          )}
        </div>
      </div>
      <div className={`sidebar-section ${isExpanded ? 'expanded' : ''}`}>
        <div className="user">
          <img src={offline} alt="offline" className="status-icon" />
          {isExpanded && (
            <>
              <span className="status">Offline</span>
              <span>420</span>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default RightSidebar;
